}

export class AzureStorageService {
  private config: AzureConfig;
  private blobBaseUrl: string;
  private userId: string;

  constructor(config: AzureConfig, userId: string) {
    this.config = config;
    this.userId = userId;
    this.blobBaseUrl = `https://${config.storageAccount}.blob.core.windows.net`;

    // Validate SAS token format
    this.validateConfig();
  }

  private getSasTokenForUrl(): string {
    // Remove leading ? if present since we'll add it in URL construction
    return this.config.sasToken.startsWith('?') ? this.config.sasToken.substring(1) : this.config.sasToken;
  }

  private validateConfig(): void {
    if (!this.config.sasToken) {
      throw new Error('SAS token is required');
    }

    // Just do basic format validation - check if it looks like a query string
    const hasEqualsSign = this.config.sasToken.includes('=');

    if (!hasEqualsSign) {
      throw new Error('SAS token format appears invalid. Should contain key=value pairs.');
    }

    // Check if SAS token starts with ? (if not, add it)
    if (!this.config.sasToken.startsWith('?')) {
      this.config.sasToken = '?' + this.config.sasToken;
    }
  }

  /**
   * Test connection to Azure Blob Storage
   */
  async testConnection(): Promise<boolean> {
    try {
      // Test blob storage connection by trying to list blobs in the specific container
      // This requires less permissions than listing all containers
      const url = `${this.blobBaseUrl}/${this.config.containerName}?restype=container&comp=list&${this.getSasTokenForUrl()}`;

      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'x-ms-version': '2020-04-08'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Azure connection test failed:', {
          status: response.status,
          statusText: response.statusText,
          url: url.replace(this.config.sasToken, '[SAS_TOKEN_HIDDEN]'),
          error: errorText
        });

        // Throw specific error for better user feedback
        if (response.status === 404) {
          throw new Error(`Container '${this.config.containerName}' not found. Create it in Azure Portal first.`);
        } else if (response.status === 403) {
          throw new Error('SAS token lacks required permissions. Generate new token with read/write/list permissions.');
        } else if (response.status === 401) {
          throw new Error('SAS token is invalid or expired. Generate a new token.');
        } else {
          throw new Error(`Azure error ${response.status}: ${errorText}`);
        }
      }

      console.log('Azure connection test successful');
      return true;
    } catch (error) {
      console.error('Azure connection test failed:', error);
      throw error; // Re-throw to show specific error message
    }
  }

  /**
   * Initialize storage (no-op since container is created manually)
   */
  async initializeStorage(): Promise<void> {
    // Container should be created manually in Azure Portal
    // No table storage needed - we store everything in blobs
  }

  /**
   * Create a backup of all user data with optional encryption
   */
  async createBackup(onProgress?: (progress: MigrationProgress) => void, pin?: string): Promise<string> {
    try {
      onProgress?.({ phase: 'backup', current: 0, total: 100, message: 'Starting backup...' });

      // Get all data from IndexedDB
      const friends = await db.friends.toArray();
      const encounters = await db.encounters.toArray();
      const interactionTypes = await db.interactionTypes.toArray();
      const settings = await db.settings.toArray();

      onProgress?.({ phase: 'backup', current: 30, total: 100, message: 'Preparing backup data...' });

      const timestamp = Date.now();
      const needsEncryption = shouldEncryptBackup();
      const backupId = needsEncryption 
        ? `${this.userId}_backup_${timestamp}_encrypted.json`
        : `${this.userId}_backup_${timestamp}.json`;

      const metadata: BackupMetadata = {
        backupId,
        timestamp: new Date(timestamp).toISOString(),
        friendsCount: friends.length,
        encountersCount: encounters.length,
        interactionTypesCount: interactionTypes.length,
        settingsCount: settings.length,
        appVersion: '1.0.0',
        deviceInfo: navigator.userAgent
      };

      const rawBackupData = {
        metadata,
        friends,
        encounters,
        interactionTypes,
        settings,
        exportedAt: new Date().toISOString(),
        version: '1.0.0'
      };

      onProgress?.({ phase: 'backup', current: 60, total: 100, message: 'Encrypting backup...' });

      // Prepare backup with encryption if needed
      let finalBackupData: EncryptedBackup;
      if (needsEncryption && pin) {
        finalBackupData = await prepareBackupForExport(rawBackupData, pin);
      } else if (needsEncryption && !pin) {
        throw new Error('PIN required for encrypted Azure backup. Please provide PIN.');
      } else {
        finalBackupData = await prepareBackupForExport(rawBackupData);
      }

      onProgress?.({ phase: 'backup', current: 80, total: 100, message: 'Uploading to Azure...' });

      // Upload to Azure Blob Storage - finalBackupData is already properly formatted by prepareBackupForExport
      await this.uploadBlob(backupId, JSON.stringify(finalBackupData, null, 2));

      onProgress?.({ phase: 'backup', current: 100, total: 100, message: 'Backup completed!' });

      return backupId;
    } catch (error) {
      throw new Error(`Backup failed: ${(error as Error).message}`);
    }
  }

  /**
   * List all backups by scanning blob storage
   */
  /**
   * List blobs in the container, optionally filtered by prefix
   */
  async listBlobs(prefix?: string): Promise<string[]> {
    try {
      let url = `${this.blobBaseUrl}/${this.config.containerName}?restype=container&comp=list&${this.getSasTokenForUrl()}`;
      if (prefix) {
        url += `&prefix=${encodeURIComponent(prefix)}`;
      }

      console.log('üîç listBlobs URL:', url.substring(0, 150) + '...');
      console.log('üîç listBlobs prefix:', prefix);

      const response = await fetch(url, {
        method: 'GET'
      });

      console.log('üìä listBlobs response status:', response.status, response.statusText);

      if (!response.ok) {
        throw new Error(`Failed to list blobs: ${response.statusText}`);
      }

      const xmlText = await response.text();
      console.log('üìÑ listBlobs XML response (first 500 chars):', xmlText.substring(0, 500));

      // Parse XML response to extract blob names
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(xmlText, 'text/xml');

      const blobs = xmlDoc.querySelectorAll('Blob > Name');
      console.log('üîç Found blob Name elements:', blobs.length);

      const blobNames: string[] = [];

      blobs.forEach((blob, index) => {
        const name = blob.textContent;
        console.log(`üìÅ Blob ${index + 1}:`, name);
        if (name) {
          blobNames.push(name);
        }
      });

      console.log('‚úÖ Final listBlobs result:', blobNames);
      return blobNames;
    } catch (error) {
      console.error('‚ùå listBlobs error:', error);
      return [];
    }
  }

  /**
   * List all backups (legacy method for compatibility)
   */
  async listBackups(): Promise<string[]> {
    return this.listBlobs();
  }

  async listBackupsWithInfo(prefix?: string): Promise<BackupInfo[]> {
    try {
      let url = `${this.blobBaseUrl}/${this.config.containerName}?restype=container&comp=list&${this.getSasTokenForUrl()}`;
      if (prefix) {
        url += `&prefix=${encodeURIComponent(prefix)}`;
      }

      const response = await fetch(url, {
        method: 'GET'
      });

      if (!response.ok) {
        throw new Error(`Failed to list blobs: ${response.statusText}`);
      }

      const xmlText = await response.text();

      // Parse XML response to extract blob metadata
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(xmlText, 'text/xml');

      const blobs = xmlDoc.querySelectorAll('Blob');
      const backupInfos: BackupInfo[] = [];

      blobs.forEach((blob) => {
        const nameElement = blob.querySelector('Name');
        const sizeElement = blob.querySelector('Properties > Content-Length');
        const lastModifiedElement = blob.querySelector('Properties > Last-Modified');

        if (nameElement?.textContent) {
          const name = nameElement.textContent;
          const size = sizeElement?.textContent ? parseInt(sizeElement.textContent) : 0;
          const lastModified = lastModifiedElement?.textContent ? new Date(lastModifiedElement.textContent) : new Date();

          backupInfos.push({ name, size, lastModified });
        }
      });

      // Sort by lastModified descending (newest first)
      return backupInfos.sort((a, b) => b.lastModified.getTime() - a.lastModified.getTime());
    } catch (error) {
      console.error('‚ùå Error listing backup info:', error);
      throw error;
    }
  }

  async restoreFromBackup(
    backupId: string,
    onProgress?: (progress: MigrationProgress) => void,
    pin?: string
  ): Promise<void> {
    try {
      onProgress?.({ phase: 'restore', current: 0, total: 100, message: 'Downloading backup...' });

      // Download backup data
      const backupJson = await this.downloadBlob(backupId);
      const rawBackupData = JSON.parse(backupJson);

      onProgress?.({ phase: 'restore', current: 20, total: 100, message: 'Decrypting backup...' });

      // Handle encrypted vs unencrypted backups
      let actualBackupData: Record<string, any>;
      if (typeof rawBackupData === 'object' && rawBackupData.encrypted === true) {
        if (!pin) {
          throw new Error('PIN required to decrypt Azure backup');
        }
        actualBackupData = await extractBackupFromExport(rawBackupData, pin);
      } else if (typeof rawBackupData === 'object' && rawBackupData.encrypted === false) {
        actualBackupData = await extractBackupFromExport(rawBackupData);
      } else {
        // Legacy unencrypted backup format
        actualBackupData = rawBackupData;
      }

      onProgress?.({ phase: 'restore', current: 40, total: 100, message: 'Clearing existing data...' });

      // Clear existing data
      await db.friends.clear();
      await db.encounters.clear();
      await db.interactionTypes.clear();
      await db.settings.clear();

      onProgress?.({ phase: 'restore', current: 60, total: 100, message: 'Restoring data...' });

      // Restore data
      if (actualBackupData.friends?.length) {
        await db.friends.bulkAdd(actualBackupData.friends);
      }
      if (actualBackupData.encounters?.length) {
        await db.encounters.bulkAdd(actualBackupData.encounters);
      }
      if (actualBackupData.interactionTypes?.length) {
        await db.interactionTypes.bulkAdd(actualBackupData.interactionTypes);
      }
      if (actualBackupData.settings?.length) {
        await db.settings.bulkAdd(actualBackupData.settings);
      }

      onProgress?.({ phase: 'restore', current: 100, total: 100, message: 'Restore completed!' });
    } catch (error) {
      throw new Error(`Restore failed: ${(error as Error).message}`);
    }
  }

  async deleteBackup(backupId: string): Promise<void> {
    try {
      await this.deleteBlob(backupId);
    } catch (error) {
      throw new Error(`Delete backup failed: ${(error as Error).message}`);
    }
  }

  // Blob Storage operations
  async uploadBlob(blobName: string, content: string): Promise<void> {
    const url = `${this.blobBaseUrl}/${this.config.containerName}/${blobName}?${this.getSasTokenForUrl()}`;

    const response = await fetch(url, {
      method: 'PUT',
      headers: {
        'x-ms-blob-type': 'BlockBlob',
        'Content-Type': 'application/json'
      },
      body: content
    });

    if (!response.ok) {
      throw new Error(`Failed to upload blob: ${response.statusText}`);
    }
  }

  private async downloadBlob(blobName: string): Promise<string> {
    const url = `${this.blobBaseUrl}/${this.config.containerName}/${blobName}?${this.getSasTokenForUrl()}`;

    const response = await fetch(url, {
      method: 'GET'
    });

    if (!response.ok) {
      throw new Error(`Failed to download blob: ${response.statusText}`);
    }

    return await response.text();
  }

  private async deleteBlob(blobName: string): Promise<void> {
    const url = `${this.blobBaseUrl}/${this.config.containerName}/${blobName}?${this.getSasTokenForUrl()}`;

    const response = await fetch(url, {
      method: 'DELETE'
    });

    if (!response.ok && response.status !== 404) {
      throw new Error(`Failed to delete blob: ${response.statusText}`);
    }
  }
}

/**
 * Generate anonymous user ID for Azure partitioning
 */
export function generateUserId(): string {
  return 'user_' + Math.random().toString(36).substr(2, 16);
}

/**
 * Validate Azure configuration
 */
export function validateAzureConfig(config: Partial<AzureConfig>): string[] {
  const errors: string[] = [];

  if (!config.storageAccount) {
    errors.push('Storage Account name is required');
  }

  if (!config.sasToken) {
    errors.push('SAS Token is required');
  }

  if (!config.containerName) {
    errors.push('Container name is required');
  }

  if (config.storageAccount && !/^[a-z0-9]{3,24}$/.test(config.storageAccount)) {
    errors.push('Storage Account name must be 3-24 characters, lowercase letters and numbers only');
  }

  if (config.containerName && !/^[a-z0-9-]{3,63}$/.test(config.containerName)) {
    errors.push('Container name must be 3-63 characters, lowercase letters, numbers, and hyphens only');
  }

  return errors;
}

import { db } from '../db/schema';
import { prepareBackupForExport, extractBackupFromExport, shouldEncryptBackup, type EncryptedBackup } from './encryption';
import { getPinForAutoBackups } from './pinManager';

import type { Friend, Encounter, InteractionType, Settings } from '../db/schema';

export interface BackupData {
  version: string;
  timestamp: string;
  friends: Friend[];
  encounters: Encounter[];
  interactionTypes: InteractionType[];
  settings: Settings[];
  includesPhotos: boolean;
}

export async function createBackup(includePhotos: boolean = true): Promise<BackupData> {
  try {
    // Get all data from IndexedDB
    const friends = await db.friends.toArray();
    const encounters = await db.encounters.toArray();
    const interactionTypes = await db.interactionTypes.toArray();
    const settings = await db.settings.toArray();

    // Process friends to handle photos
    const processedFriends = friends.map(friend => {
      if (!includePhotos) {
        // Remove photo data but keep references
        return {
          ...friend,
          avatarUrl: friend.avatarUrl?.startsWith('data:') ? '[PHOTO_REMOVED]' : friend.avatarUrl,
          photos: friend.photos ? friend.photos.map(photo => 
            photo.startsWith('data:') ? '[PHOTO_REMOVED]' : photo
          ) : undefined
        };
      }
      return friend;
    });

    // Process encounters to handle photos
    const processedEncounters = encounters.map(encounter => {
      if (!includePhotos) {
        // Remove photo data but keep references
        return {
          ...encounter,
          photos: encounter.photos ? encounter.photos.map(photo => 
            photo.startsWith('data:') ? '[PHOTO_REMOVED]' : photo
          ) : undefined
        };
      }
      return encounter;
    });

    return {
      version: '1.0.0',
      timestamp: new Date().toISOString(),
      friends: processedFriends,
      encounters: processedEncounters,
      interactionTypes,
      settings,
      includesPhotos: includePhotos
    };
  } catch (error) {
    console.error('Error creating backup:', error);
    throw new Error('Failed to create backup');
  }
}

export async function exportToFiles(includePhotos: boolean = true): Promise<void> {
  try {
    const backupData = await createBackup(includePhotos);

    // Check if we need to encrypt
    const needsEncryption = shouldEncryptBackup();
    let pin: string | null = null;

    if (needsEncryption) {
      // Get stored PIN (should be available since user unlocked the app)
      pin = await getPinForAutoBackups();

      if (!pin) {
        throw new Error('PIN not available. Please unlock the app first.');
      }
    }

    let finalData: EncryptedBackup | BackupData;
    let filename: string;
    const timestamp = new Date().toISOString().slice(0, 19).replace(/[:.]/g, '-');
    const sizeIndicator = includePhotos ? 'full' : 'no-photos';

    if (needsEncryption && pin) {
      // Create encrypted backup
      finalData = await prepareBackupForExport(backupData, pin);
      filename = `the-load-down-backup-${timestamp}-${sizeIndicator}-encrypted.json`;
    } else {
      // Create unencrypted backup - just the raw data
      finalData = backupData;
      filename = `the-load-down-backup-${timestamp}-${sizeIndicator}.json`;
    }

    const blob = new Blob([JSON.stringify(finalData, null, 2)], {
      type: 'application/json'
    });

    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  } catch (error) {
    console.error('Error downloading backup:', error);
    throw error;
  }
}

export async function restoreFromBackup(backupData: any, pin?: string): Promise<void> {
  try {
    // Clear existing data (ask for confirmation first)
    if (!confirm('This will replace ALL existing data. Are you sure?')) {
      return;
    }

    let actualBackupData: BackupData;

    // Check if this is an encrypted backup
    if (typeof backupData === 'object' && backupData.encrypted === true) {
      if (!pin) {
        throw new Error('PIN required to decrypt backup');
      }
      actualBackupData = await extractBackupFromExport(backupData, pin);
    } else if (typeof backupData === 'object' && backupData.encrypted === false) {
      actualBackupData = await extractBackupFromExport(backupData);
    } else {
      // Legacy unencrypted backup format
      actualBackupData = backupData;
    }

    // Clear all tables
    await db.friends.clear();
    await db.encounters.clear();
    await db.interactionTypes.clear();
    await db.settings.clear();

    // Restore data
    if (actualBackupData.friends?.length > 0) {
      await db.friends.bulkAdd(actualBackupData.friends);
    }

    if (actualBackupData.encounters?.length > 0) {
      await db.encounters.bulkAdd(actualBackupData.encounters);
    }

    if (actualBackupData.interactionTypes?.length > 0) {
      await db.interactionTypes.bulkAdd(actualBackupData.interactionTypes);
    }

    if (actualBackupData.settings?.length > 0) {
      await db.settings.bulkAdd(actualBackupData.settings);
    }

    alert('Backup restored successfully!');
  } catch (error) {
    console.error('Error restoring backup:', error);
    throw new Error('Failed to restore backup: ' + (error as Error).message);
  }
}

export function getBackupSize(backupData: BackupData): { size: number; sizeFormatted: string } {
  const jsonString = JSON.stringify(backupData);
  const size = new Blob([jsonString]).size;

  const formatSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return {
    size,
    sizeFormatted: formatSize(size)
  };
}

// Get auto backup settings from Azure config
function getAutoBackupSettings() {
  const saved = localStorage.getItem('azure-backup-config');
  if (!saved) {
    return { enabled: false, retentionCount: 10, container: 'auto-backups' };
  }

  try {
    const config = JSON.parse(saved);
    return {
    enabled: config.autoBackupEnabled || false,
    retentionCount: config.autoBackupRetention || 10,
    container: config.autoBackupContainer || config.containerName || 'backups'
    };
  } catch {
    return { enabled: false, retentionCount: 10, container: 'auto-backups' };
  }
}

